'use strict';

$(document).ready(function () {
    $('.news-item').on('click', function () {
        $(this).children('.news_item__text').toggle();
        $(this).children('.news-item__full-text').toggle();
    });
});
'use strict';

$(document).ready(function () {
    $('.tabber__pickers-tab').on('click', function () {
        $('.tabber__pickers-tab--active').removeClass('tabber__pickers-tab--active');
        $(this).addClass('tabber__pickers-tab--active');
        $('.tabber_text-holder').addClass('hidden');
        $('#' + $(this).attr('data-target')).removeClass('hidden');
    });
});
